import React from "react";
import { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { Button, Checkbox } from "@mui/material";

import { Delete } from "@mui/icons-material";
import EditIcon from "@mui/icons-material/Edit";
function HomePage() {
  const [toDoList, setToDoList] = useState([]);
  const location = useLocation();

  const navigate = useNavigate();

  //Extract the username from the token stored in local storage
  const token = localStorage.getItem("accessToken");
  const decodedToken = token ? JSON.parse(atob(token.split(".")[1])) : null;
  /*
  The atob function is a built-in JavaScript function that decodes a base64-encoded string.
  In the case of JWTs, the token consists of three parts separated by dots (.). 
  The second part (index 1) is the payload, which is a base64-encoded JSON string 
  containing the claims. atob is used to decode this base64-encoded payload.
  */
  const username = decodedToken ? decodedToken.username : null;
  const handleLogout = () => {
    //Remove the token from local storage
    localStorage.removeItem("accessToken");

    //Redirect to the login page
    navigate("/");
  };

  const navigateToLogin = () => {
    navigate("/");
  };
  return (
    <div>
      <h1>
        {username
          ? `Hello, ${username}! Welcome to your dashboard,`
          : `Please Login to view your homepage`}
      </h1>
      <>
        {username ? (
          <button onClick={handleLogout}>Logout</button>
        ) : (
          <button onClick={navigateToLogin}>Log In</button>
        )}
      </>
    </div>
  );
}

export default HomePage;
